import React, { useState } from "react";
import Header from "../components/header";
import styles from "./login.module.css";
import { Link } from "react-router-dom";

function Login() {
  const[uID, setUID] = useState("");
  const uIDChange = (e) => {
    setUID(e.target.value);
  }
  const [passwd, setPasswd] = useState("");
  const passwdChange = (e) => {
    setPasswd(e.target.value);
  }


  const submitHandler = (e) => {
    alert("로그인 성공!");
    return (
      <div>{uID} {passwd}</div>
    );
  }


  const [passwordType, setPasswordType] = useState({
    type: 'password',
    visible: false
  });
  const handlePasswordType = e => {
    setPasswordType(() => {
        if (!passwordType.visible) {
            return { type: 'text', visible: true };
        }
        return { type: 'password', visible: false };
    })
  } 

  return (
    <>
      <Header />
      <div className={styles.loginSection}>
        <div className={styles.idInputWrap}>
          <span>로그인</span>



            <input onChange={uIDChange}
            type="text" name="id" placeholder="아이디" maxLength="30"/>
            <input onChange={passwdChange}
            type={passwordType.type} name="pw" placeholder="비밀번호"
            maxLength="150" />


            <div className={styles.hidePwWrap}>
            {console.log(uID)}
            {console.log(passwd)}
              <img onClick={handlePasswordType}
              src={require("../images/eye_button.png")} alt="pwHide"/>
              {passwordType.type === "password" 
              ? <h onClick={handlePasswordType}>비밀번호 보기</h>
              : <h onClick={handlePasswordType}>비밀번호 숨기기</h>
              }
            </div>
            <button onChange={submitHandler}
            className={styles.loginBtn}>
              로그인
            </button>


          <Link to="/signup">
            <button className={styles.signupBtn}>
              회원가입
            </button>
          </Link>
        </div>
      </div>
    </>
  );
}
export default Login;
