import React from "react";
import styles from "./putItem.module.css"
import { useState } from "react";

function PutItem({id, name, price, size, color, stock, quan, onClick}) {
  

  return (
    <div className={styles.putItemDiv}>
      <div className={styles.nameDiv}>{name} ({size}, {color})</div>
      <div className={styles.quantDiv}>


        <button type="button" aria-label="수량 내리기"
        onClick={() => onClick(-1)}>-</button>
        <input onChange={propFunction} value={quan}
        type="number" min={1} max={stock}></input>
        <button type="button" aria-label="수량 올리기"
        onClick={() => onClick(1)}>+</button>




      </div>
      <div className={styles.priceDiv}>{price}원</div>
    </div>
  );
}

export default PutItem;
