import React from "react";
import Header from "../components/header";
import styles from "./product.module.css"

function Product() {
  const sizeOptions = [
    { value: "M", name: "M" },
    { value: "X", name: "X" },
    { value: "XL", name: "XL" },
  ];
  const colorOptions = [
    { value: "화이트", name: "화이트" },
    { value: "블랙", name: "블랙" },
  ];

  const SelectBox = (props) => {
    return (
      <select>
        {props.options.map((option) => (
          <option
            key={option.value}
            value={option.value}
          >
            {option.name}
          </option>
        ))}
      </select>
    );
  };

  return (
    <>
      <Header />
      <div className={styles.productSection}>
        <div className={styles.productImgWrap}>
          <div style={{fontSize:'17px'}}>전체 카테고리 &gt;</div>
          <img src={require('../images/sample.jpg')} alt='sample' />
          
        </div>
        <div className={styles.productInfoWrap}>
          
          <h1 style={{fontSize:'23px', fontWeight:'bold'}}>상품명</h1>
          <hr/>

          <div style={{display:'flex', backgroundColor:'tomato'}}>
            <div className={styles.productInfoTag}>
              <ul>
                <li>&gt; 성별</li>
                <li>&gt; 판매가</li>
                <li>&gt; 배송비</li>
                <li>&gt; 재고</li>
                <li>&gt; 사이즈</li>
                <li>&gt; 색상</li>
              </ul>
            </div>

            <div className={styles.productInfo}>
              <ul>
                <li>ex.남</li>
                <li>ex.12,345원</li>
                <li>ex.12,34원</li>
                <li>ex.7</li>
                <SelectBox options={sizeOptions}></SelectBox>
                <SelectBox options={colorOptions}></SelectBox>
              </ul>
            </div>
          </div>
          

        </div>  
      </div>
    </>
  )
}
export default Product;
