import React, {useState} from "react";
import Header from "../components/header";
import styles from "./myPage.module.css";
import { Link } from "react-router-dom";
import { useParams } from "react-router-dom";

function MyPage() {

  let { menu } = useParams();

  let [btnActive, setBtnActive] = useState("");

    const toggleActive = (e) => {
      setBtnActive((prev) => {
        return e.target.value;
      });
    };

  return (
    <>
      <Header />
      <div className={styles.mypgSection}>
        <div className={styles.menuWrap}>
          <h1>마이페이지</h1>


          {menu === "myInfo" ? <div>내 정보</div> 
          : <Link to="/myPage/myInfo">
            <div>내 정보</div>
          </Link>
          }
          {menu === "modUserInfo" ? <div>회원 정보 수정</div> 
          : <Link to="/myPage/modUserInfo">
            <div>회원 정보 수정</div>
          </Link>
          }
          {menu === "orderHistory" ? <div>주문 내역</div> 
          : <Link to="/myPage/orderHistory">
            <div>주문 내역</div>
          </Link>
          }
          {menu === "withdrawal" ? <div>회원 탈퇴</div> 
          : <Link to="/myPage/withdrawal">
            <div key={menu}
          className={"btn" + (menu == btnActive ? " active" : "")}
              onClick={toggleActive}>회원 탈퇴</div>
          </Link>
          }

          

      
        </div>
        <div className={styles.mypgWrap}>
          {menu === "myInfo" ?
            <div className={styles.mypgHeader}>내 정보</div>



            : (menu === "modUserInfo" ? 
            <div className={styles.mypgHeader}>회원 정보 수정</div>





            : (menu === "orderHistory" ? 
            <div className={styles.mypgHeader}>주문 내역</div> 




            : <div className={styles.mypgHeader}>회원 탈퇴</div>




            ))
          }

          
          
        </div>
      </div>
    </>
  );
}
export default MyPage;
